  var isFontFaceSupported = (function(){
  
    // Create variables for dedicated @font-face test
      var doc = document, docElement = doc.documentElement, 
          st  = doc.createElement('style'),
          spn = doc.createElement('span'),
          wid, nwid, isFakeBody = false, body = doc.body, ret;
          
    // IE supports EOT. 
    //   It has had EOT since IE 5.
    // So we sniff. :(
    if (!(!/*@cc_on@if(@_jscript_version>=5)!@end@*/0)) ret = true;
  
    else {
  
      ret = !!window.CSSFontFaceRule;
      
      // The following is a font-face + glyph definition for the . character:
      st.textContent = "@font-face{font-family:testfont;src:url('data:font/ttf;base64,')}";
      doc.getElementsByTagName('head')[0].appendChild(st);
      
      
      spn.setAttribute('style','font:99px _,serif;position:absolute;visibility:hidden');
      
      if  (!doc.body){
        body = docElement.appendChild(doc.createElement('fonttest'));
        isFakeBody = true;
      } 
      
      // the data-uri'd font only has the . glyph; which is 3 pixels wide.
      spn.innerHTML = '........';
      spn.id        = 'fonttest';
      
      body.appendChild(spn);
      wid = spn.offsetWidth;
      spn.style.fontFamily = 'testfont,_,serif';
      
      // needed for the false positives (ff3, chrome)
      ret = wid !== spn.offsetWidth;
      
      // firefox doesn't load the datauri'd font synchronously (the others do)
      // https://bugzilla.mozilla.org/show_bug.cgi?id=512566
      //isFakeBody && setTimeout(function(){body.parentNode.removeChild(body);},50);
      
      
      
    }
      
    return function(){ return ret || wid !== spn.offsetWidth; };
  })();
  