function isFontFaceSupported(){

  // IE supports EOT. 
  //   It has had EOT since IE 5.
  // So we sniff. 
  if (!(!/*@cc_on@if(@_jscript_version>=5)!@end@*/0)) return true;
  
  var doc = document,
      b   = doc.body,
      st  = doc.createElement('style'),
      spn = doc.createElement('span'),
      wid, nwid;
      
  st.textContent = "@font-face { font-family: testfont; src: url('data:font/ttf;base64,')}";
  
  spn.setAttribute('style','font-family: monospace; position: absolute; top: -9999px; left: -9999px; visibility : hidden');

  // the data-uri'd font only has the m and w glyphs.
  spn.innerHTML = '........';
  spn.id        = 'fonttest';
  
  b.appendChild(st);
  b.appendChild(spn);
  
  wid = spn.offsetWidth;
  spn.style.fontFamily = 'testfont,monospace';
  nwid = spn.offsetWidth;
  
  b.removeChild(st);
  b.removeChild(spn);
  
  return wid !== nwid;
}
